import React from 'react'
import styled from 'styled-components'

const FeedHeader = () => {

  const Container = styled.div`
    padding: 2rem;
    margin-bottom: 2px;
    border-radius: 20px 20px 0 0;
    background-color: #393945;
  `;

  const RowOne = styled.div`
    display: flex;
    justify-content: space-between;
  `;
  const Title = styled.div`
    margin-right: 2.5rem;
    font-size: 24px;
    font-weight: 400;
  `;

  const Nav = styled.div`
    display: flex;
    border-radius: 30px;
    background-color: #32323e;
    width: auto;
  `;
  const Button = styled.div`
    display: inline;
    font-size: 12px;
    padding: 8px 16px;
    border-radius: 30px;
    font-weight: 700;
    width: inherit;
    overflow: visible;
    margin: 0;
    text-align: center;
    word-spacing: normal;
    width: auto;
  `;

  const RowTwo = styled.div`
    display: flex;
    justify-content: flex-end;
    margin-top: 16px;

  `;

  const DropDown = styled.div`
    display: inline-block;
    font-size: 12px;
    background-color: #4d505f;
    border-radius: 15px;
    padding: 0.45rem 0.8rem 0.4rem;
  `;

  const Select = styled.select`
    background-color: #4d505f;
    border-radius: 15px;
    border: none;
    color: white;
    font-size: 14px;
    height: 14px;
    display: flex;
  `;
  return (
    <Container>
      <RowOne>
        <Title>Feed</Title>
        <Nav>
          <Button>Following</Button>
          <Button>Top</Button>
          <Button>Macro</Button>
          <Button>Events</Button>
        </Nav>
      </RowOne>
      <RowTwo>
        <DropDown>
          <i style={{ color: 'white' }} class="fas fa-filter"></i>
          <Select>
            <option>All Articles</option>
            <option>Premium Articles</option>
          </Select>
        </DropDown>
      </RowTwo>
    </Container>
  );
}

export default FeedHeader;
