import React, { useState } from 'react';
import styled from 'styled-components'
import SearchIconSVG from '../../images/searchIcon.svg'
import InfoIconSVG from '../../images/infoicon01.svg'
import ReactTooltip from 'react-tooltip';

const HomeSearchBar = () => {


  const InputContainer = styled.div`
    display: flex;
    align-items: center;
    border-radius: 20px;
    padding-left: 3rem;
    background-color: #393945;
    &:hover{
      background-color:#40424f;
    }
  `;

  const SearchIcon = styled.img`
    height: 18px;
    width: 18px;
  `;

  const InfoIcon = styled.img`
    height: 16px;
    width: 16px;
    margin-left: 8px;
  `;

  const SearchBarInput = styled.input`
  display: inline-block;
  width: 100%;
  padding: 1.1rem 1rem;
  background-color: inherit;
  border: none;
  font-size: 18px;
  font-weight: 200;
  line-height: 1.15;
  color: white;
  &:focus{
    border: none;
    outline: none;
  }
  &:active{
    border: none;
  }
  `;

  const InfoIconContainer = styled.div`
    padding-right: 3rem;
  `;

  const [input, setInput] = useState('exmaple12');
  console.log(input)


  return (
    <InputContainer>
      <SearchIcon src={SearchIconSVG} alt='' />
      <SearchBarInput
        type='text'
        placeholder='Search...'
      // value={} 

      />
      <InfoIconContainer>
        <ReactTooltip
          backgroundColor='white'
          textColor='black'
          place='left'
          multiline='true'
          html='true' />
        <InfoIcon data-tip="<div style={{height:'100px',width:'100px'background-color: 'white', opacity: 1, padding: '1rem'}}>
        <div style={{marginBottom: '0px',fontSize:'4em'}}>What can I search for?</div>
      <div style={{ fontSize:'300px'height: '1.5rem' }}></div><div>Companies</div><div>Find companies (stocks) by searching for a ticker or company name.</div><div>ETFs and Mutual Funds</div><div>ETFs and Mutual funds represent ways to own a diversified bundle of stocks or other assets. Find funds by searching for a ticker or company name.</div><div >Institutional Investors</div><div>Institutional investors like hedge funds, holding companies, and more are required to file their public equity holdings with the SEC.</br> You can see their disclosed holdings on Atom. Search for institutional investors by name.</div><div >Sectors</div><div>You can search for sectors of the market, such as Technology or Airlines, and find pre-made hubs for those sectors created by the Atom team.</div></div>"


          src={InfoIconSVG} alt='' />
      </InfoIconContainer>
    </InputContainer>
  );
}

export default HomeSearchBar;
