import React from 'react';
import styled from 'styled-components'

const QuoteOverview = props => {
  const Container = styled.div`
    padding : 2rem 2.5rem 0px;
    margin-bottom: 1rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    background-color: white;
  `;
  const Content = styled.div`
  `;
  const RowOne = styled.div`
    display: flex;
    flex-grow: 1;
    margin-bottom: 6px;
    color: black;
  `;

  const RowTwo = styled.div`

  `;
  console.log(props)
  return (
    <>
      {props.stockData ?
        <Container>
          <Content>
            <RowOne>
              <div>
                <span style={{ fontSize: '28px' }}>{props.stockData.companyName}</span>
                <span style={{ fontSize: '16px', marginRight: '12px', marginLeft: '4px' }}>
                  {(props.stockData.changePercent * 100).toFixed(2)}%
                </span>
                <span style={{ fontSize: '16px', marginRight: '15px', marginLeft: '0.3rem' }}></span>
              </div>
              {props.stockData.latestPrice}
            </RowOne>




            <RowTwo>
              {props.stockData.change}
            </RowTwo>
          </Content>
        </Container>
        : <></>}
    </>
  );
}

export default QuoteOverview;
