import React from 'react'
import styled from 'styled-components'



const HomeMyStockHeader = () => {


  const Header = styled.div`
  padding : 2rem 3rem;
  border: 1px solid white;

`;
  const Title = styled.h2`
  border: 1px solid black;
  margin: 0;
  color: #8f94ab;
  font-size: 24px;
  font-weight: 400;
  `;
  const RowOne = styled.div`
    display: flex;
    border : 1px solid black;
    flex-grow: 1; 
    justify-content: space-between;
`;

  const ToggleTray = styled.div`
    border: 1px solid black;
    display: flex;
    width: auto;
    border-radius: 30px;
    background-color: #32323e;
    color: white;
  `;

  const Button = styled.div`
    display: inline;
    color: #b4b8cd;
    font-size: 12px;
    padding: 8px 16px;
    border-radius: 30px;
    font-weight: 700px;
    &:hover{
      cursor: pointer;
      background-color:#4d505f;
    }
  `;

  const RowTwo = styled.div`
  display: flex;
  flex-grow: 1;
  justify-content: flex-end;
    margin-top: 1rem;
    margin-bottom: -1.5rem;
`;

  const DropDownButton = styled.div` 
    border: 1px solid black;
    display: inline-block;
    border-radius: 15px;
    padding: 0.45rem 1.2rem 0.4rem;
    font-size: 12px;
    font-weight: bold;
  `;

  return (
    <Header>





      <RowOne>
        <Title>My Stocks</Title>
        <ToggleTray>
          <Button>My Stocks</Button>
          <Button>Top Movers</Button>
          <Button>Trending</Button>
        </ToggleTray>
      </RowOne>




      <RowTwo>

        <DropDownButton style={{ color: 'white' }}> <i class="fas fa-filter"></i>

All</DropDownButton>


      </RowTwo>
    </Header>

  );
}

export default HomeMyStockHeader;
