import React from 'react';
import styled from 'styled-components'

const QuoteOverview = props => {
  const Container = styled.div`
    padding : 2rem 2.5rem 0px;
    margin-bottom: 1rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    background-color: white;
    border-radius: 20px 20px 0 0;
    background-color: #393945;
    color: white;
  `;
  const Content = styled.div`
  `;
  const RowOne = styled.div`
    display: flex;
    justify-content: space-between;
    flex-grow: 1;
    margin-bottom: 6px;
    color: black;
  `;

  const RowTwo = styled.div`

  `;
  const greenOrRed = () => (props.stockData.changePercent * 100).toFixed(2) > 0 ? 'green' : 'red'
  const caretUpOrDown = () => (props.stockData.changePercent * 100).toFixed(2) > 0 ? <i style={{
    color: 'green'
  }} class='fas fa-caret-up' /> : <i style={{
    color: 'red'
  }} class='fas fa-caret-down' />
  console.log(props)
  return (
    <>
      {props.stockData ?
        <Container>
          <Content>
            <RowOne>
              <div style={{ display: 'block', color: 'white' }}>
                <span style={{ fontSize: '28px' }}>{props.stockData.companyName}</span>
                <span style={{ fontSize: '16px', marginRight: '12px', marginLeft: '4px', color: greenOrRed() }}>{caretUpOrDown}
                  {(props.stockData.changePercent * 100).toFixed(2)}%
                </span>
                <span style={{ fontSize: '16px', marginRight: '15px', marginLeft: '0.3rem' }}> {props.stockData.latestPrice}</span>
              </div>

              <div style={{ textAlign: 'right', fontSize: '16px', color: 'white' }}>
                1Y Return:<i style={{
                  color: (props.stockData.ytdChange * 100).toFixed(2) > 0 ? 'green' : 'red'
                }} class='fas fa-caret-up' />
                <span>{(props.stockData.ytdChange * 100).toFixed(2)}%</span>
              </div>
            </RowOne>




            <RowTwo>
              {props.stockData.change}
            </RowTwo>
          </Content>
        </Container>
        : <></>}
    </>
  );
}

export default QuoteOverview;
