import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import axios from 'axios'
import './MarketOverview.scss'
const MarketPriceTable = () => {


  const Container = styled.div`
    display: flex;
    flex-grow: 1;
    margin-right: -20px;

    background-color: white;
    `;
  //tiles
  const MarketTile = styled.div`
    display: flex;
    padding: 12px 16px;
    border: 1px solid black;
    `;

  const MarketTileRow = styled.div`
    display: flex;
    justify-content: space-between;
  `;

  const MarketTileIndexName = styled.div`
    align-items: center;
    font-size: 14px;
    font-weight: bold;
  `;

  //tiles
  const PairContainer = styled.div`
  display: flex;
  margin: 0 20px 10px 0;
  `;



  const [marketOverviewData, setMarketOverviewData] = useState({})
  const fourMarkets = ['SPY', 'QQQ', 'IWM', 'DIA']
  useEffect(() => {

    const FMP_API_KEY = '0dafce6ce2fa49c8f0acd0ac316dfa33';
    const fetchStockData1 = async () => {
      const response1 = await axios.get(`https://financialmodelingprep.com/api/v3/company/profile/spy?apikey=${FMP_API_KEY}`);
      console.log(response1.data)
      setMarketOverviewData(response1.data)
    }



    fetchStockData1()
  }, [])
  console.log(marketOverviewData)

  return (
    <Container>


      <PairContainer>
        {fourMarkets.slice(0, 2).map(ele =>
          <>

            {/* <div style={{ width: '3px', borderLeft: '1px solid purple' }}></div> */}
            <MarketTile>


              <MarketTileRow>
                <MarketTileIndexName>S&P 500</MarketTileIndexName>
              </MarketTileRow>

              <MarketTileRow>

                <div>Pre Market</div>
                <div style={{ color: 'white' }}>{marketOverviewData.profile.changesPercentage}</div>
              </MarketTileRow>


            </MarketTile>

          </>

        )}

      </PairContainer>


      <PairContainer>
        <MarketTile>3</MarketTile>
        <MarketTile>4</MarketTile>
      </PairContainer>


    </Container>
  );
}

export default MarketPriceTable;
