  const InputContainer = styled.div`
    display: flex;
    align-items: center;
    border-radius: 20px;
    padding-left: 3rem;
    background-color: #393945;
  `;

  const SearchBarInput = styled.input`
  display: inline-block;
  padding: 1.1rem 1rem;
  background-color: inherit;
  border: none;
  font-size: 18px;
  line-height: 1.15;
  color: white;
  &:focus{
    border: none;
    outline: none;
  }
  &:active{
    border: none;
  }
  `;

