import React, { useState, useEffect } from 'react'
import styled from 'styled-components'
import axios from 'axios'
import './MarketOverview.scss'





// fourMarkets.map(ele => {
//   const fetchStockData = async () => {
//     const response = await axios.get(`https://financialmodelingprep.com/api/v3/company/profile/${ele}?apikey=${FMP_API_KEY}`);
//     marketDataArray.push(response.data.profile)
//   }
//   fetchStockData()
// })


const MarketPriceTable = () => {


  const Container = styled.div`
    display: flex;
    flex-grow: 1;
    margin-right: -20px;

    background-color: white;
    `;
  //tiles
  const MarketTile = styled.div`
    display: flex;
    padding: 12px 16px;
    border: 1px solid black;
    `;

  const MarketTileRow = styled.div`
    display: flex;
    justify-content: space-between;
  `;

  const MarketTileIndexName = styled.div`
    align-items: center;
    font-size: 14px;
    font-weight: bold;
  `;

  //tiles
  const PairContainer = styled.div`
  display: flex;
  margin: 0 20px 10px 0;
  `;



  const [marketOverviewData, setMarketOverviewData] = useState([])
  const fourMarkets = ['SPY', 'QQQ', 'IWM', 'DIA']
  let marketDataArray = []
  useEffect(() => {

    const FMP_API_KEY = '0dafce6ce2fa49c8f0acd0ac316dfa33';
    const IEX_API_KEY = 'pk_3256652724eb490abdfd234401050f50';



    fourMarkets.map(async ele => {
      const fetchStockData = async () => {
        const response = await axios.get(`https://cloud.iexapis.com/stable/stock/${ele}/quote?token=${IEX_API_KEY}`)
        setMarketOverviewData([...marketOverviewData, response.data])
        console.log(response.data)
      }
      fetchStockData()
    })

    setMarketOverviewData(marketDataArray)

  }, [])
  console.log(marketOverviewData)


  return (marketDataArray ?
    <Container>


      <PairContainer>
        {/* {fourMarkets.slice(0, 2).map(ele =>
          <>

            <MarketTile>


              <MarketTileRow>
                <MarketTileIndexName>S&P 500</MarketTileIndexName>
              </MarketTileRow>

              <MarketTileRow>

                <div>Pre Market</div>

                <div style={{ color: 'black', fontSize: '18px', height: '30px', width: '60px' }}>{console.log(marketDataArray.changesPercentage)}</div>
              </MarketTileRow>


            </MarketTile>

          </>

        )} */}

      </PairContainer>


      <PairContainer>
        <MarketTile>3</MarketTile>
        <MarketTile>4</MarketTile>
      </PairContainer>


    </Container> : <></>
  );
}

export default MarketPriceTable;
