  // Header
  const Header = styled.div`
height: 56px;
min-height: 56px;
display: flex;
align-items: center;
border-top-width: 2px;
border-top-color: #32323e;
color: #8f94ab;
width: 100%;
`;

  const HeaderItem = styled.div`
display: inline-flex;
/* position: absolute; */
white-space: nowrap;
font-size: 14px;
padding-left:2rem;
/* overflow: hidden; */
align-items: center;
height: 100%;
`;

  // const ScrollMenu = styled.div`
