  //   color: #8f94ab;
  //   width: 100%;
  //   `;
  // 


  const list = ['Symbol', 'Price', 'Today % Change', '1M Return', 'YTD Return', 'Market Cap', 'Entprs. Val(EV)', 'Margin', 'Growth', 'Value', 'Low'];

  const menu = list.map((ele, i) =>
    // <HeaderItem text={ele} key={i} selected={i} />
    <HeaderItem>{ele}</HeaderItem>
  )




  return (
    <ScrollMenu data={menu}>
      {/* <HeaderItem style={{ left: 10, width: '100px' }}>WWWWW</HeaderItem> */}

    </ScrollMenu>

  );
}

